import UIKit

class LoginWorker
{
  func verifyUrl(urlString: String?) -> Bool {
        guard let urlString = urlString,
            let url = URL(string: urlString) else {
                return false
            }
        return UIApplication.shared.canOpenURL(url)
    }
}
