import UIKit

protocol TasksDetailPresentationLogic
{
  func presentSomething(response: TasksDetail.Something.Response)
}

class TasksDetailPresenter: TasksDetailPresentationLogic
{
  weak var viewController: TasksDetailDisplayLogic?
  
  // MARK: Do something
  
  func presentSomething(response: TasksDetail.Something.Response)
  {
    let rows = ["Info", "Attachments", "History", "Rights"]
    
   if let header = response.task.name {
            
            let viewModel = TasksDetail.Something.ViewModel(sections: [TasksDetail.Something.ViewModel.TaskDetail(header: header, rows: rows)])
            viewController?.displaySomething(viewModel: viewModel)
        } else {
            let viewModel = TasksDetail.Something.ViewModel(sections: [TasksDetail.Something.ViewModel.TaskDetail(header: "", rows: rows)])
            viewController?.displaySomething(viewModel: viewModel)
        }
      }
    }
