import UIKit

protocol TDInfoPresentationLogic
{
  func presentSomething(response: TDInfo.Something.Response)
}

class TDInfoPresenter: TDInfoPresentationLogic
{
  weak var viewController: TDInfoDisplayLogic?
  
  // MARK: Do something
  
  func presentSomething(response: TDInfo.Something.Response)
  {
    if let name = response.task.name {
    let sections = TDInfo.Something.ViewModel(sections: [
        TDInfo.Something.ViewModel.Item(header: name, rows: [])
        ,TDInfo.Something.ViewModel.Item(header: "Info", rows: [response.task])
        ])
    let viewModel = sections
    viewController?.displaySomething(viewModel: viewModel)
    }
  }
}
