import UIKit

protocol ToDosDetailPresentationLogic
{
  func presentSomething(response: ToDosDetail.Something.Response)
}

class ToDosDetailPresenter: ToDosDetailPresentationLogic
{
  weak var viewController: ToDosDetailDisplayLogic?
  
  // MARK: Do something
  
  func presentSomething(response: ToDosDetail.Something.Response)
  {
    let rows = ["Info", "Attachments", "History", "Rights"]
    
   if let header = response.task.name {
            
            let viewModel = ToDosDetail.Something.ViewModel(sections: [ToDosDetail.Something.ViewModel.ToDoDetail(header: header, rows: rows)])
            viewController?.displaySomething(viewModel: viewModel)
        } else {
            let viewModel = ToDosDetail.Something.ViewModel(sections: [ToDosDetail.Something.ViewModel.ToDoDetail(header: "", rows: rows)])
            viewController?.displaySomething(viewModel: viewModel)
        }
      }
    }
